mapper.select:
select
	
from leaves t

	
		and t.course_id = #{courseId}			
	
	
		and t.user_id = #{userId}

select
	
from leaves t
where t.id = #{value}

mapper.update:
update leaves
course_id = #{courseId}, course_name = #{courseName}, user_id = #{userId}, user_name = #{userName}, leave_time = #{leaveTime}, leave_no = #{leaveNo}, reason = #{reason}, leave_status = #{leaveStatus}, create_user = #{createUser}, create_date = #{createDate}, update_user = #{updateUser}, update_date = #{updateDate},
where id = #{id}
